'use strict';

module.exports = (app, module) =>
{
  module.sio.sockets.on('connection', (socket) =>
  {
    socket.on('snf.tests.getData', (reply) =>
    {
      if (typeof reply === 'function')
      {
        reply({
          currentTest: module.currentTest,
          lastTest: module.lastTest
        });
      }
    });
  });
};
